import { outdent } from 'outdent';
import { tellProcess } from '../utils/process.js';
export async function setTextFieldValue(element, text) {
    await tellProcess(element.applicationProcess, outdent `
			set value of ${element.pathString} to ${JSON.stringify(text)}
		`);
}
export async function getTextFieldValue(element) {
    const textFieldValue = await tellProcess(element.applicationProcess, outdent `
			get value of ${element.pathString}
		`);
    return textFieldValue;
}
