import { outdent } from 'outdent';
import { runAppleScript } from '../utils/run.js';
export async function waitForWindow({ windowName, processName, numAttempts = 30, interval = 0.1, }) {
    await runAppleScript(outdent `
			tell application "System Events"
				set i to 0
				repeat until exists window ${JSON.stringify(windowName)} of application process ${JSON.stringify(processName)}
					delay ${interval}
					set i to i + 1
					if i â‰¥ ${numAttempts} then return
				end repeat
			end tell
		`);
}
